              parameters: [
                string(name: 'BRANCH', value: "${SW_PLATFORM_BRANCH}"),
                string(name: 'COMPONENT_COMMITS', value: "${COMPONENT_COMMITS},host-software/esperanto-tools-libs:${BRANCH}")
              ]
          }
